<div class="modal fade" id="loginModal">	<div class="modal-dialog">		<div class="modal-content">			<!-- Modal Header -->			<div class="modal-header">				<h4 class="modal-title"><span class="fa fa-user-circle-o"></span> Đăng nhập</h4>				<button type="button" class="close" data-dismiss="modal">&times;</button>			</div>			 <!-- Modal body -->			<div class="modal-body">			 	<form method="POST" id="form-login">					{{ csrf_field() }}					<div class="form-group">						<label for="user"><span class="fa fa fa-user-circle"></span> Tài khoản:</label>						<input type="text" class="form-control" id="user" name="username_login" value="{{ old('username_login') }}" placeholder="Nhập vào tài khoản...">					</div>					<div class="form-group">						<label for="pass"><span class="fa fa-key"></span> Mật khẩu:</label>						<input type="password" class="form-control" id="pass" name="password_login" value="{{ old('password_login') }}" placeholder="Nhập vào mật khẩu...">					</div>					<div class="form-check">				    	<label class="form-check-label">				      		<input class="form-check-input" type="checkbox" name="remember_login"> Ghi nhớ				    	</label>				  	</div>					<button style="margin-top: 10px;" type="submit" id="btn-login" class="btn btn-block btn-dark"><span class="fa fa-arrow-circle-o-right"></span> Đăng nhập</button>				</form>				<hr>				<form action="{{ route('login_with_facebook') }}" method="GET">					<button class="btn btn-block btn-social btn-facebook text-white" type="submit">			        	<span class="fa fa-facebook-square fa-lg"></span> Login with Facebook			        </button>				</form>			</div>		</div>	</div></div>